/*COMMON*/
body {
  color: #333;
  font-size: 16px;
  font-weight: 400;
  line-height: 1.4;
  font-family: 'Nanum Gothic', sans-serif;
}

img {
  display: block; /*이미지는 글자 취급을 안하고 블록으로 처리하겠다는 뜻*/
}

a {
  text-decoration: none;
}

/*HEADER*/
header {
  background-color: #f6f5f0;
  border-bottom: 1px solid #c8c8c8;
}

header .inner {
  width: 1100px;
  height: 120px;
  margin: 0 auto; /*가운데 정렬*/
  position: relative;
}

header .logo {
  height: 75px;
  position: absolute; /*부모 요소 기준으로 선언하겠다. 즉 부모요소에도 relative 가 있어야함*/
  top: 0;
  bottom: 0;
  margin: auto;
}

header .sub-menu {
  display: flex;
  position: absolute; /*부모 요소는 inner*/
  top: 10px;
  right: 0;
}

header .sub-menu ul.menu{
  font-family: Arial, Helvetica, sans-serif;
  display: flex;
}

header .sub-menu ul.menu li {
  position: relative;
}

header .sub-menu ul.menu li::before{
  content: "";
  display: block;
  width: 1px;
  height: 12px;
  background-color: #e5e5e5;
  position: absolute;
  top: 0;
  bottom: 0;
  margin: auto ;

}

header .sub-menu ul.menu li:first-child:before{ /*sign in 옆 구분 선 없애는 코드*/
  display: none;
}

header .sub-menu ul.menu li a {
  font-size: 12px;
  padding: 11px 16px;
  display: block;
  color: #656565;
}

header .sub-menu ul.menu li a:hover{
  color: #000;
}

header .sub-menu .search {
  height: 34px;
  position: relative;
}

header .sub-menu .search input{
  width: 36px;
  height: 34px;
  padding: 4px 10px;
  border: 1px solid #ccc;
  box-sizing: border-box;
  border-radius: 5px;
  outline: none;
  background-color: #fff;
  color: #777;
  font-size: 12px;
  transition: width .4s;
}

header .sub-menu .search input:focus{
  width: 190px;
  border-color: #669900;
}

header .sub-menu .search .material-icons{
  height: 24px;
  position: absolute;
  top: 0;
  bottom: 0;
  right: 5px;
  margin: auto;
  transition: .5s; /*0.5초만에 사라지고 나타나기*/
}

header .sub-menu .search.focused .material-icons {
  opacity: 0; /*검색 아이콘 지우기*/
}
